targets.rawSql:
select processlist_user as username, processlist_host as host, processlist_time as seconds,  processlist_state as state, processlist_info as commands
from performance_schema.threads 
where thread_id in (select blocking_thread_id FROM performance_schema.data_LOCK_WAITS);

select processlist_user as username, processlist_host as host, processlist_time as seconds,  processlist_state as state, processlist_info as commands
from performance_schema.threads 
where thread_id in (select requesting_thread_id FROM performance_schema.data_LOCK_WAITS);
